function checkIfIframeLoaded(iframe) {
    console.log('checkIfIframeLoaded');
    var iframeDoc = iframe.contentDocument || iframe.contentWindow.document;
    var radioSlotObj = $("input[name='time']", iframeDoc);

    if(radioSlotObj != undefined){
        radioSlotObj.click(function() {
            var dateValueNext, timeValueNext;
            var radioDateVal = $("input[name='weekview']:checked", iframeDoc);
            if(radioDateVal != undefined){
                dateValueNext = radioDateVal.next("div.day-date").find(".slotDate").text();
                var radioSlotVal = $("input[name='time']:checked", iframeDoc);
                if(radioSlotVal != undefined){
                    timeValueNext = radioSlotVal.next("div.time").find("em").text();
                    var startDate = moment(dateValueNext + " " + timeValueNext).format('MM/DD/YYYY h:mm a');
                    var endDate = moment(startDate).add(1, 'hours').format('MM/DD/YYYY h:mm a');
                    setDateControlFieldValue("msdynce_scheduledstart",startDate);
                    setDateControlFieldValue("msdynce_scheduledend", endDate); 
                    sessionStorage.setItem("selectedDateTime",  moment(dateValueNext + " " + timeValueNext).format('MMM DD hh:mm a'));
                    $("#msdynce_selectedtimeslot").val( moment(dateValueNext + " " + timeValueNext).format('MMM DD, YYYY | hh:mm A'));
                }
            }
        });  
    }
}

function setDateControlFieldValue(fieldname,d){
   //debugger;
   var datefield = $('#'+ fieldname);
