function getAppointmentForReview(activityid) {
  var showData = '';
  return appAjax('Loading...', {
    type: 'GET',
    url: '/_api/appointments',
    contentType: 'application/json',
    success: function (response) {
      var showData = '';
      if (response.value.length > 0) {
        var appointmentFilter = response.value.filter(function (p) {
          return p.activityid == activityid;
        });
        var meetURL = window.parent.location.origin + '/page/admincancel/?id=' + activityid;
        var rescheduleURL = window.parent.location.origin + '/page/adminreschedule/?id=' + activityid;
        var editAppointment = appointmentFilter[0];
        var meetTime = {
          todayDate: '',
          meetstart: '',
          meetend: '',
        };
        meetTime.todayDate = moment(new Date(editAppointment.scheduledstart)).format('MMM DD, YYYY');
        meetTime.meetstart = moment(new Date(editAppointment.scheduledstart)).format('h:mm a');
        meetTime.meetend = moment(new Date(editAppointment.scheduledend)).format('h:mm a');

        $('#meeting-date').html(meetTime.todayDate + ' ' + meetTime.meetstart + ' - ' + meetTime.meetend);
        $('#subcategory').html(editAppointment.subservicecategory);
        $('#bank-name-location').html(editAppointment.bankname);
        $('#bank-name-address').html(editAppointment.bankaddress);
        $('#contact-name').html(editAppointment.firstname + ' ' + editAppointment.lastname);
        $('#contact-email').html(editAppointment.emailaddress);
        $('#contact-phone').html(editAppointment.phone);
        $('#description').html(decodeURIComponent(editAppointment.description));
        $('#location-option').html(editAppointment.locationoption);
        $('#cancel-meeting-url').attr('href', meetURL);
        $('#reschedule-url').attr('href', rescheduleURL);

        showData +=
          '<div class="everyday-banking-content">' +
          '<ul>' +
          '<li class="eb-icon eb-one"><strong id="meeting-date">' +
          meetTime.todayDate +
          ' ' +
          meetTime.meetstart +
          ' - ' +
          meetTime.meetend +
          '</strong></li>' +
          '<li class="eb-icon eb-two"><strong id="subcategory"></strong></li>' +
          '<li class="eb-icon eb-thr"><strong id="contact-name"></strong><span id="contact-email"></span><span id="contact-phone"></span></li>' +
          '<li class="eb-fou"><strong>Is there anything else you would like us to know?</strong><span id="description"></span></li>' +
          '</ul>' +
          '<ul>' +
          '<li class="eb-fiv"><a id="cancel-meeting-url">Cancel Meeting</a>' +
          '<a id="reschedule-url">Reschedule</a>' +
