function createCalendar(){    
    daysOfYear.forEach((date,index)=>{ 
        var timeSlots = [];
        if(rData !=undefined){
            // var selectedDayAppointmentsArray = rData.filter(function(p)
            var selectedDayAppointmentsArray = rData.value.filter(function(p)
            {
                return (moment(p.scheduledstart).format('MM/DD/YYYY') ==  moment(date).format('MM/DD/YYYY')) && 
                ($.inArray(moment(p.scheduledstart).format('h:mm a'), avSlots) != -1) &&
                p.msdyn_bankappointmenttype != 639040001;
            });
            var selectedDayUnavailabilityArray = rData.value.filter(function(p)
            {
                return (moment(p.scheduledstart).format('MM/DD/YYYY') ==  moment(date).format('MM/DD/YYYY')) && 
                ($.inArray(moment(p.scheduledstart).format('h:mm a'), avSlots) != -1) &&
                p.msdyn_bankappointmenttype == 639040001;
            }); 

            if(selectedDayAppointmentsArray!=undefined && selectedDayAppointmentsArray.length>0){          
                var appointments = selectedDayAppointmentsArray.length;                 
                var slotday = date.toString().substring(8,10);
                $("#slot_" + slotday).text(appointments + " appointments"); 
                var slotObj={
                    slotType:1,            
                    bookedSlots:selectedDayAppointmentsArray
                };                
                timeSlots.push(slotObj);
            }

            if(selectedDayUnavailabilityArray!=undefined && selectedDayUnavailabilityArray.length>0){
                var slotObj={
                    slotType:2,            
                    bookedSlots:selectedDayUnavailabilityArray
                };                
                timeSlots.push(slotObj);                            
            }
        }

        if(unAvailabilityData != undefined){
            var selectedDayUnavailabilityArray = unAvailabilityData.filter(function(p)
            {
                return (moment(p.scheduledstart).format('MM/DD/YYYY') ==  moment(date).format('MM/DD/YYYY')) && 
                ($.inArray(moment(p.scheduledstart).format('h:mm a'), avSlots) != -1) ;
            }); 
            if(selectedDayUnavailabilityArray!=undefined && selectedDayUnavailabilityArray.length>0){
                var slotObj={
                    slotType:2,            
                    bookedSlots:selectedDayUnavailabilityArray
                };                
                timeSlots.push(slotObj);                            
            } 
        }
        if(timeSlots.length>0)
        {
            createTimeSlotCards(date,timeSlots);
        }else
        {
            createEmptySlots(date);
        }            
    });
}

