function createTimeSlotCards(date, timeSlots){
    var tempDate="";
    var dateId = date.toString().substring(4,10).replace(' ','');
    var formattedDate = moment(date).format('MMDD');              
    tempDate+= '<ul id="day-'+ dateId +'" class="day-schedule">';
    var unavlDisplayObj = {
        tempStr: "",
        blockedLiSlot: ""
    }; 
    $(avSlots).each(function(index, slotItem){        
        var appoSlot = timeSlots.filter(sl => sl.slotType == 1);
        if(appoSlot != undefined && appoSlot.length > 0)
        {
            var bslot = appoSlot[0].bookedSlots.filter(el => moment(new Date(el.scheduledstart+" UTC")).format('h:mm a') === slotItem && 
            moment(new Date(el.scheduledstart+" UTC")).format('MM/DD/YYYY') == moment(date).format('MM/DD/YYYY'));
            if(bslot!=undefined && bslot.length>0){ 
                var slotData = bslot[0];
                var dateTitle = moment(new Date(slotData.scheduledstart +" UTC")).format('h:mm a');
                var nextSlot = moment(new Date(slotData.scheduledstart +" UTC")).add(1, 'hours').format('h:mm a');
                tempDate+= '<li><div class="single-schedule"><input type="radio" name="calendarview" data-toggle="modal" data-target="#edit-calendar" id="'+slotData.activityid+'" title="'+dateTitle+'">';
                tempDate+= '<label for="calendarview" class="schedule-card available-card">';  
                tempDate+= '<div class="schedule-time">'+dateTitle+' - '+nextSlot+'</div>'; 
                tempDate+= '<div class="schedule-services '+slotData.servicecategory+'">'+slotData.subservicecategory+'</div>';
                tempDate+= '<div class="schedule-representative">'+slotData.firstname+' '+slotData.lastname+'</div>';
                tempDate+= '</label></div></li>';                
                return;
            }
        }
        var unavlSlot = timeSlots.filter(sl => sl.slotType == 2);
        if(unavlSlot != undefined && unavlSlot.length > 0)
        {
            var uslot = unavlSlot[0].bookedSlots.filter(el => moment(new Date(el.scheduledstart+" UTC")).format('h:mm a') === slotItem && 
            moment(new Date(el.scheduledstart+" UTC")).format('MM/DD/YYYY') == moment(date).format('MM/DD/YYYY'));
            if(uslot!=undefined && uslot.length>0){
                var slotData = uslot[0];
                var endDate = new Date(slotData.scheduledend);
                var startDate = new Date(slotData.scheduledstart); 
                var hours = moment.duration(moment(endDate).diff(moment(startDate))).asHours();
                if(hours == 1){                    
                    var dTitle = moment(new Date(slotData.scheduledstart +" UTC")).format('h:mm a');                      
                    var nextSlot = moment(new Date(slotData.scheduledstart +" UTC")).add(1, 'hours').format('h:mm a');
                    tempDate+= '<li class= "timecard-1"><div class="single-schedule"><input type="radio" name="calendarview" data-toggle="modal" data-target="#edit-unavailability" id="'+slotData.activityid+'" title="'+dTitle+'">';
                    tempDate+= '<label for="calendarview" class="schedule-card unavailable-card">';  
                    tempDate+= '<div class="schedule-time">'+dTitle+' - '+nextSlot+'</div>'; 
                    tempDate+= '<div class="schedule-services '+slotData.subject+'">Unavailable - '+slotData.subject+'</div>';
                    tempDate+= '<div class="schedule-representative">Me</div>';
                    tempDate+= '</label></div></li>'; 
                    return;
                } else{                                  
                    unavlDisplayObj = checkAndCreateMultiSlotCalendarBlocks(slotData,unavlDisplayObj);
                    tempDate+=  unavlDisplayObj.tempStr; 
                    return;                     
                }          
            }           
        }
        if(slotItem.indexOf(unavlDisplayObj.blockedLiSlot)!== -1){
            tempDate+= '<li></li>';
        }        
    });
    
    tempDate+= '</ul>';  
    $('#date-'+dateId).append(tempDate);   
    
}

function checkAndCreateMultiSlotCalendarBlocks(slotData,unavlDisplayObj){
    var endDate = new Date(slotData.scheduledend);
    var startDate = new Date(slotData.scheduledstart); 
    var hours = moment.duration(moment(endDate).diff(moment(startDate))).asHours();
    var totalslots = Math.floor(hours/8);     
    var currSlotDate = moment(new Date(slotData.scheduledstart +" UTC")).format('MM/DD/YYYY');
    var endSlotTime = "4:00 pm";
    var currDaySlotEndDateTime = currSlotDate + " " + endSlotTime; 
    var startSlotDateTime = moment(new Date(slotData.scheduledstart +" UTC")).format('MM/DD/YYYY h:mm a'); 
    var endSlotDateTime = moment(new Date(slotData.scheduledend +" UTC")).format('MM/DD/YYYY h:mm a');
    var currDayDur = moment.duration(moment(currDaySlotEndDateTime).diff(moment(startSlotDateTime))).asHours();    
    var liStr = "";
    unavlDisplayObj.tempStr = "";
    unavlDisplayObj.blockedLiSlot="";
    if(totalslots<=1 && hours<=currDayDur){ 
        var className = "timecard-" + hours;
        var dTitle = moment(new Date(slotData.scheduledstart +" UTC")).format('h:mm a');                      
        var nextSlot = moment(new Date(slotData.scheduledstart +" UTC")).add(hours, 'hours').format('h:mm a');
        liStr+= '<li class="'+ className + '"><div class="single-schedule"><input type="radio" name="calendarview" data-toggle="modal" data-target="#edit-unavailability" id="'+slotData.activityid+'" title="'+dTitle+'">';
        liStr+= '<label for="calendarview" class="schedule-card unavailable-card">';  
        liStr+= '<div class="schedule-time">'+dTitle+' - '+nextSlot+'</div>'; 
        liStr+= '<div class="schedule-services '+slotData.subject+'">Unavailable - '+slotData.subject+'</div>';
        liStr+= '<div class="schedule-representative">Me</div>';
        liStr+= '</label></div></li>'; 
        unavlDisplayObj.tempStr = liStr;
        var slotStr = dTitle;
        var stDt = moment(new Date(slotData.scheduledstart +" UTC"));
        for(i=0;i<hours; i++){
            slotStr += "," + stDt.add(1, 'hours').format('h:mm a');
        }
        unavlDisplayObj.blockedLiSlot = slotStr;
        return unavlDisplayObj;
    }
    else{
    } 
    return unavlDisplayObj ; 
         
}

$(document).ready(function () {
				 
    var itemsMainDiv = ('.MultiCarousel');
    var itemsDiv = ('.MultiCarousel-inner');
    var itemWidth = "";
	
	$('.leftLst, .rightLst').click(function () {
		var condition = $(this).hasClass("leftLst");
		if (condition)
			click(0, this);
		else
			click(1, this)
	});

	ResCarouselSize();

    $(window).resize(function () {
        ResCarouselSize();
    });

       //this function define the size of the items
    function ResCarouselSize() {
        var incno = 0;
        var dataItems = ("data-items");
        var itemClass = ('.days-box');
        var id = 0;
        var btnParentSb = '';
        var itemsSplit = '';
        var sampwidth = $(itemsMainDiv).width();
        var bodyWidth = $('body').width();
        $(itemsDiv).each(function () {
            id = id + 1;
            var itemNumbers = $(this).find(itemClass).length;
            btnParentSb = $(this).parent().attr(dataItems);
            itemsSplit = btnParentSb.split(',');
            $(this).parent().attr("id", "MultiCarousel" + id);
            if (bodyWidth >= 1200) {
                incno = itemsSplit[3];
                itemWidth = sampwidth / incno;
            }
            else if (bodyWidth >= 992) {
                incno = itemsSplit[2];
                itemWidth = sampwidth / incno;
            }
            else if (bodyWidth >= 768) {
                incno = itemsSplit[1];
                itemWidth = sampwidth / incno;
            }
            else {
                incno = itemsSplit[0];
                itemWidth = sampwidth / incno;
            }
            $(this).css({ 'transform': 'translateX(0px)', 'width': itemWidth * itemNumbers });
            $(this).find(itemClass).each(function () {
                $(this).outerWidth(itemWidth);
            });

            $(".leftLst").addClass("over");
            $(".rightLst").removeClass("over");
        });
    }

        //this function used to move the items
    function ResCarousel(e, el, s) {
        var leftBtn = ('.leftLst');
        var rightBtn = ('.rightLst');
        var translateXval = '';
        var divStyle = $(el + ' ' + itemsDiv).css('transform');
        var values = divStyle.match(/-?[\d\.]+/g);
        var xds = Math.abs(values[4]);
        if (e == 0) {
            translateXval = parseInt(xds) - parseInt(itemWidth * s);
            $(el + ' ' + rightBtn).removeClass("over");

            if (translateXval <= itemWidth / 2) {
                translateXval = 0;
                $(el + ' ' + leftBtn).addClass("over");
            }
        }
        else if (e == 1) {
            var itemsCondition = $(el).find(itemsDiv).width() - $(el).width();
            translateXval = parseInt(xds) + parseInt(itemWidth * s);
            $(el + ' ' + leftBtn).removeClass("over");

            if (translateXval >= itemsCondition - itemWidth / 2) {
                translateXval = itemsCondition;
                $(el + ' ' + rightBtn).addClass("over");
            }
        }
        $(el + ' ' + itemsDiv).css('transform', 'translateX(' + -translateXval + 'px)');
    }

    //It is used to get some elements from btn
    function click(ell, ee) {
        var Parent = "#" + $(ee).parent().attr("id");
        var slide = $(Parent).attr("data-slide");
        ResCarousel(ell, Parent, slide);
    }

    $(".prev-button").attr("disabled","true");
});

