{% assign poll_name = poll_name %}
{% assign poll_placement_name = poll_placement_name %}

{% if poll_name %}
  {% assign poll_by_name = polls[poll_name] %}
  <div class="poll" data-url="{{ poll_by_name.poll_url }}" data-submit-url="{{ poll_by_name.submit_url }}"></div>
{% elseif poll_placement_name %}
  {% assign placement = polls.placements[poll_placement_name] %}
  {% include "Random Poll" placement:placement %}
{% endif %}

{% extends 'Layout 1 Column' %}

{% block main %}
{% if sitemap.root.children.size > 0 %}
  {% assign leaf_page = false %}
{% else %}
  {% assign leaf_page = true %}
{% endif %}

<ul class="tree" role="navigation">
  <li class="root">
    <a href="{{ sitemap.root.url | h }}" title="{{ sitemap.root.title | h }}">
      {{ sitemap.root.title | h }}
    </a>
    {% unless leaf_page %}
      <ul>
        {% for child in sitemap.root.children %}
          {% include 'Sitemap Item' %}
        {% endfor %}
      </ul>
    {% endunless %}
  </li>
</ul>
{% endblock %}

{% unless placement %}
  {% if id %}
    {% assign placement = ads.placements[id] %}
  {% elsif name %}
    {% assign placement = ads.placements[name] %}
  {% endif %}
{% endunless %}

{% if placement %}
<div class="ad" data-url="{{ placement.random_url | h }}"></div>
{% endif %}

{% extends 'Layout 1 Column' %}
{% block main %}
  {% include 'Page Copy' %}
<div class="container" role="main">
    {% include 'Child Navigation' showdescriptions: true %}
    {% if page.adx_entitylist %}
    {% include 'entity_list' key: page.adx_entitylist.id %}
    {% endif %}

    {% if page.adx_entityform %}
    {% entityform id: page.adx_entityform.id %}
    {% endif %}

    {% if page.adx_webform %}
    {% webform id: page.adx_webform.id %}
    {% endif %}
</div>
{% endblock %}

{%raw%}
 <script id="facets-view-pagination" type="text/x-handlebars-template">
  {{#if needsPagination}}
   <ul class="pagination">
    {{#ifvalue pageNumber value=1}}
     <li class=" page-item disabled"><span class="aspNetDisabled" aria-label="{%endraw%}{{ resx.Pagination_First_Page }}{%raw%}">«</span></li>
     <li class=" page-item disabled"><span class="aspNetDisabled" aria-label="{%endraw%}{{ resx.Pagination_Previous_Page }}{%raw%}">‹</span></li>
    {{else}}
     <li><a class=" page-item  page-link js-go-to-1" aria-label="{%endraw%}{{ resx.Pagination_First_Page }}{%raw%}" href="">«</a></li>
     <li><a class=" page-item  page-link js-go-to-{{previousPage}}" aria-label="{%endraw%}{{ resx.Pagination_Previous_Page }}{%raw%}" href="">‹</a></li>
    {{/ifvalue}}
    {{#each pageLinks}}
     {{#ifvalue this value=../pageNumber}}
      <li class=" page-item active" aria-label="{{ stringFormat "
       {%endraw%}{{ resx.Pagination_Current_Page }}{%raw%}" this}}" tabIndex="0"><span>{{this}}</span></li>
     {{else}}
      <li><a class=" page-item  page-link js-go-to-{{this}}" aria-label="{{ stringFormat "
       {%endraw%}{{ resx.Pagination_Page }}{%raw%}" this}}" href="">{{this}}</a></li>
     {{/ifvalue}}
    {{/each}}

    {{#ifvalue pageNumber value=pageCount}}
     <li class=" page-item disabled"><span aria-label="{%endraw%}{{ resx.Pagination_Next_Page }}{%raw%}">›</span></li>
     <li class=" page-item disabled"><span aria-label="{%endraw%}{{ resx.Pagination_Last_Page }}{%raw%}">»</span></li>
    {{else}}
     <li><a class=" page-item  page-link js-go-to-{{nextPage}}" aria-label="{%endraw%}{{ resx.Pagination_Next_Page }}{%raw%}" href="">›</a></li>
     <li><a class=" page-item  page-link js-go-to-{{pageCount}}" aria-label="{%endraw%}{{ resx.Pagination_Last_Page }}{%raw%}" href="">»</a></li>
    {{/ifvalue}}
   </ul>
  {{/if}}
 </script>
 {% endraw %}

{% extends 'Layout 2 Column Wide Left' %}

{% block main %}
  {% include 'Page Copy' %}
  {% include 'Child Navigation' showdescriptions: true %}
{% endblock %}

{% block aside %}
  {% include "Weblink List Group" weblink_set_name: "Secondary Navigation" showdescriptions: true %}
  {% include "poll" poll_placement_name: "Sidebar" %}
  {% include "ad" ad_placement_name: "Sidebar Bottom" %}
{% endblock %}

{%raw%}
 <script id="facets-view-results" type="text/x-handlebars-template">
  {{#each facetViews}}

  {{#ifvalue facetName value="_logicalname"}}
   {{#if facetData}}
    <div class="facet-view short-list card     ">
     <div class="facet-title card-header">{%endraw%}{{ snippets['Search/Facet/RecordType'] | default: resx['Facet_RecordType'] | h }}{%raw%}</div>
     <div class="card-body">
      <ul class="facet-list-group"
       role="listbox" aria-label="{%endraw%}{{ snippets['Search/Facet/RecordType'] | default: resx['Facet_RecordType'] | h }}{%raw%}">
       {{#each facetData}}
        <li class="facet-list-group-item clearfix control-item {{#if active}}active{{/if}}"
         tabIndex="0"
         data-facet="{{../facetName}}" data-control-value="{{name}}"
         role="option" aria-label="{{labelText}}" title="{{labelText}}" aria-selected="{{#if active}}true{{else}}false{{/if}}" aria-checked="{{#if active}}true{{else}}false{{/if}}">
         <span class="facet-list-group-item-count float-end">{{hitCount}}</span>
         <div class="facet-list-group-item-title-container">
          <span class="facet-list-group-item-title float-start">{{displayName}}</span>
         </div>
        </li>
       {{/each}}
       <button type="button" class="show-more btn btn-link btn-xs" tabIndex="0">{%endraw%}{{ snippets['Search/Facet/More'] | default: resx['Facet_Show_More'] | h }}{%raw%}</button>
       <button type="button" class="show-less btn btn-link btn-xs" tabIndex="0">{%endraw%}{{ snippets['Search/Facet/Less'] | default: resx['Facet_Show_Less'] | h }}{%raw%}</button>
      </ul>
     </div>
    </div>
   {{/if}}
  {{/ifvalue}}

  {{#ifvalue facetName value="modifiedon.date"}}
  {{#if noData}}
  {{else}}
   <div class="facet-view card     ">
    <div class="facet-title card-header">{%endraw%}{{ snippets['Search/Facet/ModifiedDate'] | default: resx['Facet_DateModified'] | h }}{%raw%}</div>
    <div class="card-body">
     <ul class="facet-list-group"
      role="listbox" aria-label="{%endraw%}{{ snippets['Search/Facet/ModifiedDate'] | default: resx['Facet_DateModified'] | h }}{%raw%}">
      {{#each facetData}}
       <li class="facet-list-group-item clearfix control-item {{#if active}}active{{/if}}" tabIndex="0" data-facet="{{../facetName}}" data-control-value="{{name}}"
        role="option" aria-label="{{labelText}}" title="{{labelText}}"  aria-checked="{{#if active}}true{{else}}false{{/if}}">
        <span class="facet-list-group-item-count float-end">{{hitCount}}</span>
        <div class="facet-list-group-item-title-container">
         <span class="facet-list-group-item-title float-start">{{displayName}}</span>
        </div>
       </li>
      {{/each}}
     </ul>
    </div>
   </div>
  {{/if}}
  {{/ifvalue}}

  {{#ifvalue facetName value="rating"}}
   {{#if facetData}}
    <div class= "facet-view short-list card     ">
     <div class="facet-title card-header">{%endraw%}{{ snippets['Search/Facet/Rating'] | default: resx['Facet_Rating'] | h }}{%raw%}</div>
     <div class="card-body">
      <ul class="facet-list-group rating-facet-group"
       role="listbox" aria-label="{%endraw%}{{ snippets['Search/Facet/Rating'] | default: resx['Facet_Rating'] | h }}{%raw%}">
       {{#each facetData}}
        {{#if skipStars}}
         <li class="facet-list-group-item clearfix control-item {{#if ../noActive}}active{{/if}}" data-facet="{{../facetName}}" data-control-value="" tabIndex={{#if ../noActive}}0{{else}}-1{{/if}}"
          role="option" aria-label="{%endraw%}{{ snippets['Search/Facet/All'] | default: resx['Facet_All'] | h }}{%raw%} {{hitCount}}" aria-selected="{{#if  ../noActive}}true{{else}}false{{/if}}" aria-checked="{{#if  ../noActive}}true{{else}}false{{/if}}">
          <span class="facet-list-group-item-title float-start">{%endraw%}{{ snippets['Search/Facet/All'] | default: resx['Facet_All'] | h }}{%raw%}</span>
         </li>
        {{else}}
         <li class="facet-list-group-item clearfix control-item {{#if active}}active{{/if}}" data-facet="{{../facetName}}" data-control-value="{{name}}" tabIndex="{{#if active}}0{{else}}-1{{/if}}"
         role="option" aria-label="{{ratingLabel}} {{hitCount}}" aria-selected="{{#if active}}true{{else}}false{{/if}}" aria-checked="{{#if active}}true{{else}}false{{/if}}">
          <span aria-label="{{ratingLabel}}" class="facet-list-group-item-title">
           {{#each filledStars}}
            <span class="rating-star rating-star-filled"></span>
           {{/each}}
           {{#each emptyStars}}
            <span class="rating-star rating-star-empty"></span>
           {{/each}}
           <span aria-hidden="true">{%endraw%}{{resx.Facet_Rating_And_Up}}{%raw%}</span>
          </span>
          <span class="facet-list-group-item-count float-end">{{hitCount}}</span>
         </li>
        {{/if}}
       {{/each}}
      </ul>
     </div>
    </div>
   {{/if}}
  {{/ifvalue}}

  {{#ifvalue facetName value="associated.product"}}
   {{#if facetData}}
    <div class="facet-view facet-view-multiple-select short-list card     ">
     <div class="facet-title card-header">{%endraw%}{{ snippets['Search/Facet/Product'] | default: resx['Facet_Products'] | h }}{%raw%}</div>
     <div class="card-body">
      <ul class="facet-list-group" aria-label="{%endraw%}{{ snippets['Search/Facet/Product'] | default: resx['Facet_Products'] | h }}{%raw%}">
       <li class="facet-list-group-item clearfix control-item {{#if noActive}}active{{/if}}" 
        data-facet="{{../facetName}}" data-control-value="" 
        tabIndex={{#if noActive}}0{{else}}-1{{/if}}"
        aria-label="{%endraw%}{{ snippets['search/facet/all'] | default: resx['Facet_All'] | h }}{%raw%}" 
        aria-selected="{{#if  noActive}}true{{else}}false{{/if}}">
         <span class="facet-list-group-item-title float-start">{%endraw%}{{ snippets['search/facet/all'] | default: resx['Facet_All'] | h }}{%raw%}</span>
       </li>
       {{#each facetData}}
        <li class="facet-list-group-item clearfix control-item {{#if active}}active{{/if}}" 
         data-facet="{{../facetName}}" data-control-value="{{name}}"
         tabIndex="{{#if active}}0{{else}}-1{{/if}}" aria-label="{{displayName}} {{hitCount}}" 
         aria-selected="{{#if active}}true{{else}}false{{/if}}"
         role="checkbox" aria-checked="{{#if active}}true{{else}}false{{/if}}" 
         data-control-display-name="{{displayName}}">
          <span class="facet-list-group-item-count float-end">{{hitCount}}</span>
          <div class="facet-list-group-item-title-container">
           <label class="facet-list-group-item-title float-start">
            <input type="checkbox" {{#if active}}checked{{/if}} tabindex="-1" role="presentation"/>
            {{displayName}}
           </label>
          </div>
        </li>
       {{/each}}
       <button type="button" class="show-more btn btn-link btn-xs" tabIndex="0">{%endraw%}{{ snippets['Search/Facet/More'] | default: resx['Facet_Show_More'] | h }}{%raw%}</button>
       <button type="button" class="show-less btn btn-link btn-xs" tabIndex="0">{%endraw%}{{ snippets['Search/Facet/Less'] | default: resx['Facet_Show_Less'] | h }}{%raw%}</button>
      </ul>
     </div>
    </div>
   {{/if}}
  {{/ifvalue}}

  {{/each}}
 </script>

 {% endraw %}

{% assign depth_offset = depth_offset | default: 0 %}
{% assign current_page = current_page | default: page %}
{% assign current_depth = 0 %}

{% if current_page.children.size > 0 %}
  {% assign leaf_page = false %}
{% else %}
  {% assign leaf_page = true %}
{% endif %}

{% capture page_item %}
  <li class="active pr-color">
    <a href="{{ current_page.url | h }}" title="{{ current_page.title | h }}">
      {% if leaf_page %}
        <span class="fa fa-fw" aria-hidden="true"></span>
      {% else %}
        <span class="fa fa-fw" aria-hidden="true"></span>
      {% endif %}
      {{ current_page.title | h }}
    </a>
    {% unless leaf_page %}
      <ul class="pr-color">
        {% for child in current_page.children %}
        {% if child.entity.logical_name == 'adx_webpage' %}
          <li>
            <a href="{{ child.url | h }}" title="{{ child.title | h }}">
              {% if child.children.size > 0 %}
                <span class="fa fa-fw" aria-hidden="true"></span>
              {% else %}
                <span class="fa fa-fw" aria-hidden="true"></span>
              {% endif %}
              {{ child.title | h }}
            </a>
          </li>
          {% endif %}
        {% endfor %}
      </ul>
    {% endunless %}
  </li>
{% endcapture %}

<ul class="tree pr-color" role="navigation">
  {% assign crumb_count = 0 %}
  {% assign leaf_mode = false %}
  
  {% for crumb in current_page.breadcrumbs %}
    {% unless current_depth < depth_offset %}
      {% if forloop.last and leaf_page %}
        {% assign leaf_mode = true %}
      {% else %}
        <li>
          <a href="{{ crumb.url | h }}" title="{{ crumb.title | h }}">
            <span class="fa fa-fw" aria-hidden="true"></span>
            {{ crumb.title | h }}
          </a>
        </li>
      {% endif %}
      {% assign crumb_count = crumb_count | plus: 1 %}
    {% endunless %}
    {% assign current_depth = current_depth | plus: 1 %}
  {% endfor %}
  
  {% if crumb_count < 1 %}
    {{ page_item }}
  {% elsif crumb_count < 2 and leaf_mode %}
    {% for parent_sibling in current_page.parent.parent.children %}
      {% if parent_sibling.url == current_page.parent.url %}
        <li>
          <a href="{{ current_page.parent.url | h }}" title="{{ current_page.parent.title | h }}">
            <span class="fa fa-fw" aria-hidden="true"></span>
            {{ current_page.parent.title | h }}
          </a>
          <ul>
            {% for sibling in current_page.parent.children %}
            {% if sibling.entity.logical_name == 'adx_webpage' %}
              <li {% if sibling.url == current_page.url %}class="active"{% endif %}>
                <a href="{{ sibling.url | h }}" title="{{ sibling.title | h }}">
                  {% if sibling.children.size > 0 %}
                    <span class="fa fa-fw" aria-hidden="true"></span>
                  {% else %}
                    <span class="fa fa-fw" aria-hidden="true"></span>
                  {% endif %}
                  {{ sibling.title | h }}
                </a>
              </li>
              {% endif %}
            {% endfor %}
          </ul>
        </li>
      {% else %}
       {% if parent_sibling.entity.logical_name == 'adx_webpage' %}
        <li>
          <a href="{{ parent_sibling.url | h }}" title="{{ parent_sibling.title | h }}">
            {% if parent_sibling.children.size > 0 %}
              <span class="fa fa-fw" aria-hidden="true"></span>
            {% else %}
              <span class="fa fa-fw" aria-hidden="true"></span>
            {% endif %}
            {{ parent_sibling.title | h }}
          </a>
        </li>
        {% endif %}
      {% endif %}
    {% endfor %}
  {% else %}
    <li>
      <ul>
        {% if leaf_mode %}
          {% for parent_sibling in current_page.parent.parent.children %}
            {% if parent_sibling.url == current_page.parent.url %}
              <li>
                <a href="{{ current_page.parent.url | h }}" title="{{ current_page.parent.title | h }}">
                  <span class="fa fa-fw" aria-hidden="true"></span>
                  {{ current_page.parent.title | h }}
                </a>
                <ul>
                  {% for sibling in current_page.parent.children %}
                  {% if sibling.entity.logical_name == 'adx_webpage' %}
                    <li {% if sibling.url == current_page.url %}class="active"{% endif %}>
                      <a href="{{ sibling.url | h }}" title="{{ sibling.title | h }}">
                        {% if sibling.children.size > 0 %}
                          <span class="fa fa-fw" aria-hidden="true"></span>
                        {% else %}
                          <span class="fa fa-fw" aria-hidden="true"></span>
                        {% endif %}
                        {{ sibling.title | h }}
                      </a>
                    </li>
                    {% endif %}
                  {% endfor %}
                </ul>
              </li>
            {% else %}
            {% if parent_sibling.entity.logical_name == 'adx_webpage' %}
              <li>
                <a href="{{ parent_sibling.url | h }}" title="{{ parent_sibling.title | h }}">
                  {% if parent_sibling.children.size > 0 %}
                    <span class="fa fa-fw" aria-hidden="true"></span>
                  {% else %}
                    <span class="fa fa-fw" aria-hidden="true"></span>
                  {% endif %}
                  {{ parent_sibling.title | h }}
                </a>
              </li>
              {% endif %}
            {% endif %}
          {% endfor %}
        {% else %}
          {% for sibling in current_page.parent.children %}
            {% if sibling.url == current_page.url %}
              {{ page_item }}
            {% else %}
            {% if sibling.entity.logical_name == 'adx_webpage' %}
              <li>
                <a href="{{ sibling.url | h }}" title="{{ sibling.title | h }}">
                  {% if sibling.children.size > 0 %}
                    <span class="fa fa-fw" aria-hidden="true"></span>
                  {% else %}
                    <span class="fa fa-fw" aria-hidden="true"></span>
                  {% endif %}
                  {{ sibling.title | h }}
                </a>
              </li>
              {% endif %}
            {% endif %}
          {% endfor %}
        {% endif %}
      </ul>
    </li>
  {% endif %}
</ul>

{% extends "Layout 2 Column Wide Right" %}

{% block aside %}
  <div class=" d-none d-sm-block d-md-block d-lg-block hidden-xs d-print-none">
    {% include "Side Navigation" depth_offset: 1 %}
  </div>
{% endblock %}

{% assign openTag = '{{' %}
{% assign closingTag = '}}' %}
 {%raw%}
<script id="search-view-results" type="text/x-handlebars-template">
     {{#if items}}
      <div class="page-header">
        <h2 role="status" aria-live="off">{%endraw%}{{openTag}} stringFormat "{{ resx.Search_Results_Format_String }}" firstResultNumber lastResultNumber itemCount {{closingTag}}{%raw%}
        <em class="querytext">{{{query}}}</em>
       {{#if isResetVisible}}
         <a class="btn btn-default btn-sm facet-clear-all" role="button" title="{%endraw%}{{ snippets['Search/Facet/ClearConstraints'] | default: res['Search_Filter_Clear_All'] }}{%raw%}" tabIndex="0">{%endraw%}{{ snippets['Search/Facet/ClearConstraints'] | default: res['Search_Filter_Clear_All'] }}{%raw%}</a>
        {{/if}}
        </h2>
      </div>
      <div>
     <ul>
      {{#each items}}
       <li>
        <h3><a title="{{title}}" href="{{url}}">{{#if parent}}<span class="glyphicon glyphicon-file float-start text-muted" aria-hidden="true"></span>{{/if}}{{title}}</a></h3>
        <p class="fragment">{{{fragment}}}</p>
        {{#if parent}}
         <p class="small related-article">{%endraw%}{{ resx.Related_Article }}{%raw%}: <a title="{{parent.title}}" href="{{parent.absoluteUrl}}">{{parent.title}}</a></p>
        {{/if}}
        <ul class="note-group small list-unstyled">
         {{#if relatedNotes}}
          {{#each relatedNotes}}
           <li class="note-item">
           {{#if isImage}}
            <a target="_blank" title="{{title}}" href="{{absoluteUrl}}"><span class="glyphicon glyphicon-file" aria-hidden="true"></span> {{title}}</a>
           {{else}}
            <a title="{{title}}" href="{{absoluteUrl}}"><span class="glyphicon glyphicon-file" aria-hidden="true"></span> {{title}}</a>{{/if}}
           <p class="fragment text-muted">{{{fragment}}}</p>
           </li>
          {{/each}}
          {{/if}}
        </ul>
       </li>
      {{/each}}
     </ul>
    </div>
     {{else}}
     <h2 role="status">{%endraw%}{{ resx.Search_No_Results_Found }}{%raw%}<em class="querytext">{{{query}}}</em>
       {{#if isResetVisible}}
        <a class="btn btn-default btn-sm facet-clear-all" role="button" title="{%endraw%}{{ snippets['Search/Facet/ClearConstraints'] | default: res['Search_Filter_Clear_All'] }}{%raw%}" tabIndex="0">{%endraw%}{{ snippets['Search/Facet/ClearConstraints'] | default: res['Search_Filter_Clear_All'] }}{%raw%}</a>
       {{/if}}
      </h2>
     {{/if}}
</script>
 {%endraw%}

{% if ad %}
<div>
 {% if ad.redirect_url %}<a class="ad-link" href="{{ ad.redirect_url | h }}">{% endif %}
 {% if ad.title %}
 <h5 class="ad-title">{{ ad.title | h }}</h5>
 {% endif %}
 {% if ad.image.url %}
 <img class="ad-link-image mx-auto" src="{{ ad.image.url | h }}" alt="{{ ad.image.alternate_text | h }}"{% if ad.image.height %} height="{{ ad.image.height }}"{% endif %}{% if ad.image.width %} width="{{ ad.image.width }}"{% endif %} style="{% if ad.image.height %}height:{{ ad.image.height }}px;{% endif %}{% if ad.image.width %}width:{{ ad.image.width }}px;{% endif %}">
 {% endif %}
 {% if ad.redirect_url %}</a>{% endif %}
 {% if show_copy %}{% if ad.copy %}
 <div class="ad-copy">{{ ad.copy }}</div>
 {% endif %}{% endif %}
</div>
{% endif %}

{% assign weblink_set_name = weblink_set_name %}

{% assign showdescriptions = showdescriptions | default: false %}

{% if weblink_set_name %}
  {% assign weblink_set = weblinks[weblink_set_name] %}
  {% if weblink_set %}
    {% if showdescriptions %}
      <div class="{% if weblink_set.editable %}xrm-entity xrm-editable-adx_weblinkset{% endif %}">
        <div class="list-group weblinks">
          {% for link in weblink_set.weblinks %}
            <a class=" list-group-item-action list-group-item weblink" href="{{ link.url | h }}" title="{{ link.tooltip | h }}">
              <h4 class="list-group-item-heading weblink-name">{{ link.name | h }}</h4>
              <div class="list-group-item-text weblink-description">
                {{ link.description | strip_html | text_to_html  }}
              </div>
            </a>
          {% endfor %}
        </div>
        {% editable weblink_set %}
      </div>
    {% else %}
      <div class="card      {% if weblink_set.editable %}xrm-entity xrm-editable-adx_weblinkset{% endif %}">
        <div class="card-header">
          <div class="card-title"><span class="fa fa-bars" aria-hidden="true" style="font-size: 14px;"></span>  {{ snippets["Related Links Navigation Heading"] | default:"Related Links" | h }}</div>
        </div>
        <div class="weblinks list-group">
          {% for link in weblink_set.weblinks %}
            <a class=" list-group-item-action weblink list-group-item" href="{{ link.url | h }}" title="{{ link.tooltip | h }}">
              {% if link.image %}
                {% if link.image.url startswith '.' %}
                  <span class="{{ link.image.url | split:'.' | join | h }}" aria-hidden="true"></span>
                {% else %}
                  <img src="{{ link.image.url | h }}" alt="{{ link.image.alternate_text | h }}" />
                {% endif %}
              {% endif %}
              {{ link.name | h }}
            </a>
          {% endfor %}
        </div>
      {% editable weblink_set %}
      </div>
  {% endif %}
 {% endif %}
{% endif %}

{% if child.children.size > 0 %}
  {% assign leaf_page = false %}
{% else %}
  {% assign leaf_page = true %}
{% endif %}
<li {% if leaf_page %}class="node-end"{% endif %}>
  <a href="{{ child.url | h }}" title="{{ child.title | h }}">
    {{ child.title | h }}
  </a>
  {% unless leaf_page %}
    {% assign children = child.children %}
    <ul>
      {% for child in children %}
        {% include 'Sitemap Item' %}
      {% endfor %}
    </ul>
