{% block main %}
    <div class="calendar-block">{% entityform name: 'C1 Create Calendar Block' %}</div>
    <span id="letterCountRow"><span id="letterCount"></span>/1000</span>
    <script>
    $(document).ready(function(){
        var rdata;        
        $('body').addClass('header-remove');
        $("#block-calendar-modal", parent.document).css("height", "424px");        
        $('.navbar.navbar-inverse.navbar-static-top').remove(); 
        
        getAppointment();  
        // getUnavailableAppointments();     
        validateDates();
        /* $("#InsertButton").click(function(){
            if($('.validation-summary').css('display') == 'none'){
                $("#block-calendar",parent.document).removeClass("in");
                $("body",parent.document).removeClass("modal-open");
                $("body",parent.document).removeAttr("style");
                $(".modal-backdrop",parent.document).remove();
                $(".alert-calendar-success",parent.document).show();
                reload();  
            }         
        });*/
    });

    function reload(){ 
           window.parent.location.reload(true); 
    }

    function getAppointment() {
        var userId = window["Microsoft"].Dynamic365.Portal.User.contactId;
        $.ajax({
            url: `/getAllAppointments?bankRepId=${userId}`,
            dataType: 'json',
            success: function (response) {                
                rData = response; 
                                
                return response;                    
            },
            error: function (e) {
                alert(e.statusText);
            }
        });
    }

    function getUnavailableAppointments() {
        $.ajax({
            url: '/getUnavailableAppointments',
            dataType: 'json',
            success: function (response) {                
                uData = response; 
                                
                return response;                    
            },
            error: function (e) {
                alert(e.statusText);
            }
        });
    }

    function validateDates(){
        if (typeof (Page_Validators) == 'undefined') return;
         // Create new validator         
         var newValidator = document.createElement('span');
         newValidator.style.display = "none";
         newValidator.id = "scheduledendValidator";
         newValidator.controltovalidate = "scheduledend";                         
         newValidator.evaluationfunction = function () {
            var startTime = moment($("#scheduledstart_Container").find("#ComboBox7-input").val());
            var endTime = moment($("#scheduledend_Container").find("#ComboBox17-input").val());
            var startOnlyDate = moment($("#scheduledstart_Container").find("#datetimeBox0-label").val());
            var endOnlyDate = moment($("#scheduledend_Container").find("#datetimeBox0-label").val());
        
            //var startDate = moment($("#scheduledstart_Container").find("#datetimeBox0-label").val() + " " + $("#scheduledstart_Container").find("#ComboBox7-input").val()).format('MM/DD/YYYY h:mm a');
            //var endDate = moment($("#scheduledend_Container").find("#datetimeBox0-label").val() + " " + $("#scheduledend_Container").find("#ComboBox17-input").val()).format('MM/DD/YYYY h:mm a');
            var startDate = moment($("#msdynce_scheduledstart").val()).format('MM/DD/YYYY h:mm a');
            var endDate = moment($("#msdynce_scheduledend").val()).format('MM/DD/YYYY h:mm a');
            var getAppointmentDetails = rData.filter(function(p)
            {
                return (moment(new Date(p.scheduledstart +" UTC")).format('MM/DD/YYYY h:mm a') >=  moment(startDate).format('MM/DD/YYYY h:mm a')) && 
                (moment(new Date(p.scheduledend +" UTC")).format('MM/DD/YYYY h:mm a') <=  moment(endDate).format('MM/DD/YYYY h:mm a')) ;
            });
            var getUnavailableAppointments = uData.filter(function(u)
            {
                return (moment(new Date(u.scheduledstart +" UTC")).format('MM/DD/YYYY h:mm a') >=  moment(startDate).format('MM/DD/YYYY h:mm a')) && 
                (moment(new Date(u.scheduledend +" UTC")).format('MM/DD/YYYY h:mm a') <=  moment(endDate).format('MM/DD/YYYY h:mm a')) ;
            });
            if (moment(startDate).isAfter(endDate)){                
                this.errormessage = "<a href='#scheduledend_label'>Please select a different end date. End date is prior to Start date.</a>"; 
                return false;
            }else {
               var maxEndDate =  moment(startDate).add(7, 'd');
                if (moment(endDate).isAfter(maxEndDate)){
                    this.errormessage = "<a href='#scheduledend_label'>Please select a different end date. End date is more than a week from Start date.</a>"; 
                    return false;
                }else {
                    if(getAppointmentDetails.length > 0 || getUnavailableAppointments.length > 0){
                        this.errormessage = "<a href='#'>Unable to save calendar block at this time. You have an existing meeting scheduled at this time</a>"; 
                        return false;
                    }
                    else{
                    return true;
                    }
                }
            }            
        };
          
         Page_Validators.push(newValidator);          
         $("a[href='#scheduledend_label']").on("click", function () { scrollToAndFocus('scheduledend_label','escheduledend'); });    
    }

    </script>
{% endblock %}

