        <button type="button" class="calendar-block-button" onclick="showBlockCalendarModal();" data-bs-toggle="modal" data-bs-target="#block-calendar">{{ snippets["AdminHome/CalendarBlockButton"] }}</button>
        <div class="weekview-calendar-control">
            <div class="today-prev-next-button">
                <button class="today-button" type="button">{{ snippets["AdminHome/Today"] }}</button>
                <button class="prev-button" type="button"></button>
                <button class="next-button" type="button"></button>            
            </div>
            <div class="weekday-button">
                <button type="button" class="day-button" type="button">{{ snippets["AdminHome/Day"] }}</button>
                <button type="button" class="week-button view-active" type="button">{{ snippets["AdminHome/Week"] }}</button>
            </div>
        </div>

        <div class="weekview-calendar-box calendar-box">
            <div class="MultiCarousel" data-items="1,3,5,7" data-bs-slide="1" id="MultiCarousel" data-interval="1000">            
            <button class="btn btn-primary leftLst" style="display: none" type="button"></button>
            <button class="btn btn-primary rightLst" style="display: none" type="button"></button>
                <div class="MultiCarousel-inner" id="MultiCarousel-inner">       
                </div>
            
        </div>
    </div>

    <div class="modal fade calendar-block popup-custom" id="block-calendar" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">
        <div class="modal-dialog" role="document" style="max-width: 70%;">
            <div class="modal-content" style="display: block;">
                <div class="modal-header">
                    <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"><span aria-hidden="true">×</span></button>
                    <h4 class="modal-title" id="myModalLabel">{{ snippets["AdminHome/CalendarBlockModal/Header"] }}</h4>
                </div>
                <div class="modal-body">
                <iframe width="100%" height="395" id="block-calendar-modal" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen=""></iframe>
                </div>
            </div>
        </div>
    </div>

    <div class="modal fade edit-calendar popup-custom" id="edit-calendar" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">
        <div class="modal-dialog" role="document">
            <div class="modal-content-edit">
                <div class="modal-header">
                    <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"><span aria-hidden="true">×</span></button>
                    <h4 class="modal-title-app" id="edit-calendar-label"></h4>
                </div>
                <div class="modal-body">
                <iframe width="100%" height="468" id="edit-calendar-modal" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen=""></iframe>
                </div>
            </div>
        </div>
    </div>
    <div class="modal fade edit-unavailability popup-custom" id="edit-unavailability" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">
        <div class="modal-dialog" role="document">
            <div class="modal-content">
                <div class="modal-header">
                    <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"><span aria-hidden="true">×</span></button>
                    <h4 class="modal-title" id="edit-unavailability-label"></h4>
                </div>
                <div class="modal-body">
                <iframe width="100%" height="468" id="edit-unavailability-modal" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen="">
                </iframe>
                </div>
            </div>
        </div>
    </div>
