    function getAppointment() {
        var userId = window["Microsoft"].Dynamic365.Portal.User.contactId;
        var start = new Date();
        $.ajax({
            url: `/_api/appointments?$filter=_msdynce_contact_value eq ${userId}`,
            //url: `/_api/appointments?$filter=_msdynce_contact_value eq ${userId} and msdynce_statuscancel neq 441790001`,
            // url: `/_api/appointments`,
            type: "GET",
            contentType: 'application/json',
            success: function (response) {                
                rData = response; 
                createCalendar();             
                return response;                    
            },
            error: function (e) {
                alert(e.statusText);
            }
        });
    }  

    
    function createDateCards(){
        var start = new Date(); 
        start.setDate(start.getDate()); 
        var end = new Date();
        end.setDate(end.getDate() + 29);
        for (var d = start; d <= end; start.setDate(start.getDate() + 1)) {
            daysOfYear.push(new Date(start));
        }
        var temp="";
        daysOfYear.forEach((date)=>{ 
            var day=""+date;
            var dayId = day.substring(4,10).replace(' ','');
            temp+='<div class="days-box" id="date-'+dayId+'"><div class="day-box"><input type="radio" id="'+day+'" name="calendarview" title="'+ day.substring(4,10) + '" onclick="dateRadioClick(this)">';
            temp+='<label for="calendarview" class="day-date">';
            temp+='<span>'+day.substring(0,4)+'</span>';
            temp+='<em>'+day.substring(4,10)+'</em>';
            temp+='<span id="slot_'+ day.substring(8,10) + '">0 appointments</span></label></div></div>';            
        });        
        $("#MultiCarousel-inner").append(temp);
    }

    function dateRadioClick(obj) {
        var day = $(obj).next().find("em");
        $('.time-box input[type="radio"]').removeAttr("checked");
        $("div.time span").text("Available");
        checkSlots(day[0].textContent.substring(4));
        var Parent = $(obj).parents().find("div#MultiCarousel1.MultiCarousel");
        var dateDiff = calculateDateDifference(obj);
        var slide = $(Parent).attr("data-slide") * dateDiff;
        calculateItemWidth();
        ResCarousel1(obj, Parent, slide);
        $(".days-box.current-date").removeClass("current-date").nextAll().slice(0,slide).last().addClass("current-date");
        $(".prev-button").removeAttr("disabled");
    }



        function calculateDateDifference(obj) {
            var currentSelectedDateDiv = $(".days-box.current-date").children()[0];
            var currentSelectedDate = currentSelectedDateDiv.children[0].id;
            var diff = Math.round((new Date($(obj)[0].id)-new Date(currentSelectedDate))/(1000*60*60*24));
            //return new Date($(obj)[0].id) - new Date(currentSelectedDate);
            return diff;
        }

        function calculateItemWidth() {
            var itemsMainDiv = ('.MultiCarousel');
            var itemsDiv = ('.MultiCarousel-inner');
            var incno = 0;
            var dataItems = ("data-items");
            var itemClass = ('.item');
            var id = 0;
            var btnParentSb = '';
            var itemsSplit = '';
            var sampwidth = $(itemsMainDiv).width();
            var bodyWidth = $('body').width();
            $(itemsDiv).each(function() {
                id = id + 1;
                var itemNumbers = $(this).find(itemClass).length;
                btnParentSb = $(this).parent().attr(dataItems);
                itemsSplit = btnParentSb.split(',');
                $(this).parent().attr("id", "MultiCarousel" + id);
                if (bodyWidth >= 1200) {
                    incno = itemsSplit[3];
                    itemWidth = sampwidth / incno;
                } else if (bodyWidth >= 992) {
                    incno = itemsSplit[2];
                    itemWidth = sampwidth / incno;
                } else if (bodyWidth >= 768) {
                    incno = itemsSplit[1];
                    itemWidth = sampwidth / incno;
                } else {
                    incno = itemsSplit[0];
                    itemWidth = sampwidth / incno;
                }
            });
        }

        //this function define the size of the items
        function ResCarouselSize1() {
            var itemsMainDiv = ('.MultiCarousel');
            var itemsDiv = ('.MultiCarousel-inner');
            var incno = 0;
            var dataItems = ("data-items");
            var itemClass = ('.item');
            var id = 0;
            var btnParentSb = '';
            var itemsSplit = '';
            var sampwidth = $(itemsMainDiv).width();
            var bodyWidth = $('body').width();
            $(itemsDiv).each(function() {
                id = id + 1;
                var itemNumbers = $(this).find(itemClass).length;
                btnParentSb = $(this).parent().attr(dataItems);
                itemsSplit = btnParentSb.split(',');
                $(this).parent().attr("id", "MultiCarousel" + id);
                if (bodyWidth >= 1200) {
                    incno = itemsSplit[3];
                    itemWidth = sampwidth / incno;
                } else if (bodyWidth >= 992) {
                    incno = itemsSplit[2];
                    itemWidth = sampwidth / incno;
                } else if (bodyWidth >= 768) {
                    incno = itemsSplit[1];
                    itemWidth = sampwidth / incno;
                } else {
                    incno = itemsSplit[0];
                    itemWidth = sampwidth / incno;
                }
                $(this).css({
                    'transform': 'translateX(0px)',
                    'width': itemWidth * itemNumbers
                });
                $(this).find(itemClass).each(function() {
                    $(this).outerWidth(itemWidth);
                });

                $(".leftLst").addClass("over");
                $(".rightLst").removeClass("over");

            });
        }

