function createCalendar(){    
    daysOfYear.forEach((date,index)=>{ 
        var timeSlots = [];
        if(rData !=undefined){
            // var selectedDayAppointmentsArray = rData.filter(function(p)
            var selectedDayAppointmentsArray = rData.value.filter(function(p)
            {
                return (moment(p.scheduledstart).format('MM/DD/YYYY') ==  moment(date).format('MM/DD/YYYY')) && 
                ($.inArray(moment(p.scheduledstart).format('h:mm a'), avSlots) != -1) &&
                p.msdyn_bankappointmenttype != 639040001;
            });
            var selectedDayUnavailabilityArray = rData.value.filter(function(p)
            {
                return (moment(p.scheduledstart).format('MM/DD/YYYY') ==  moment(date).format('MM/DD/YYYY')) && 
                ($.inArray(moment(p.scheduledstart).format('h:mm a'), avSlots) != -1) &&
                p.msdyn_bankappointmenttype == 639040001;
            }); 

            if(selectedDayAppointmentsArray!=undefined && selectedDayAppointmentsArray.length>0){          
                var appointments = selectedDayAppointmentsArray.length;                 
                var slotday = date.toString().substring(8,10);
                $("#slot_" + slotday).text(appointments + " appointments"); 
                var slotObj={
                    slotType:1,            
                    bookedSlots:selectedDayAppointmentsArray
                };                
                timeSlots.push(slotObj);
            }

            if(selectedDayUnavailabilityArray!=undefined && selectedDayUnavailabilityArray.length>0){
                var slotObj={
                    slotType:2,            
                    bookedSlots:selectedDayUnavailabilityArray
                };                
                timeSlots.push(slotObj);                            
            }
        }

        if(unAvailabilityData != undefined){
            var selectedDayUnavailabilityArray = unAvailabilityData.filter(function(p)
            {
                return (moment(p.scheduledstart).format('MM/DD/YYYY') ==  moment(date).format('MM/DD/YYYY')) && 
                ($.inArray(moment(p.scheduledstart).format('h:mm a'), avSlots) != -1) ;
            }); 
            if(selectedDayUnavailabilityArray!=undefined && selectedDayUnavailabilityArray.length>0){
                var slotObj={
                    slotType:2,            
                    bookedSlots:selectedDayUnavailabilityArray
                };                
                timeSlots.push(slotObj);                            
            } 
        }
        if(timeSlots.length>0)
        {
            createTimeSlotCards(date,timeSlots);
        }else
        {
            createEmptySlots(date);
        }            
    });
}

function createEmptySlots(date){
    var tempDate="";
    var dateId = date.toString().substring(4,10).replace(' ','');
    var formattedDate = moment(date).format('MMMDD'); 
    tempDate+= '<ul id="day-'+ formattedDate +'" class="day-schedule">';
    for(i=1;i<=8;i++){
        tempDate+= '<li></li>';
    };
    tempDate+='</ul>';
    $('#date-'+dateId).append(tempDate);
}
 
function createTimeSlotCards(date, timeSlots){
    var tempDate="";
    var dateId = date.toString().substring(4,10).replace(' ','');
    var formattedDate = moment(date).format('MMDD');              
    tempDate+= '<ul id="day-'+ dateId +'" class="day-schedule">';
    var unavlDisplayObj = {
        tempStr: "",
        blockedLiSlot: ""
    }; 
    $(avSlots).each(function(index, slotItem){        
        var appoSlot = timeSlots.filter(sl => sl.slotType == 1);
        if(appoSlot != undefined && appoSlot.length > 0)
        {
            var bslot = appoSlot[0].bookedSlots.filter(el => moment(el.scheduledstart).format('h:mm a') === slotItem && 
            moment(el.scheduledstart).format('MM/DD/YYYY') == moment(date).format('MM/DD/YYYY'));
            if(bslot!=undefined && bslot.length>0){ 
                var slotData = bslot[0];
                var appointmentId = slotData.activityid;
                var dateTitle = moment(new Date(slotData.scheduledstart)).format('h:mm a');
                var nextSlot = moment(new Date(slotData.scheduledstart)).add(1, 'hours').format('h:mm a');
                tempDate+= '<li><div class="single-schedule"><input type="radio" name="calendarview" data-bs-toggle="modal" data-bs-target="#edit-calendar" id="'+slotData.activityid +'" title="'+dateTitle+'">';
                tempDate+= '<label for="calendarview" class="schedule-card available-card">';  
                tempDate+= '<div class="schedule-time">'+dateTitle+' - '+nextSlot+'</div>';
                tempDate+= '<div class="schedule-services '+slotData.servicecategory+'"><span class="icon-image"><img src="/generalinvesting.png" class="serviceicon" alt="service name icon"></span>'+slotData['_msdynce_services_value@OData.Community.Display.V1.FormattedValue']+'</div>';
                tempDate+= '<div class="schedule-representative"><span class="icon-image"><img src="/profile-placeholder.png" class="placeholderProfileIcon" alt="service name icon"></span>'+slotData.msdynce_firstname+' '+slotData.msdynce_lastname+'</div>';
                tempDate+= '</label></div></li>';                
                return;
            }
        }
        var unavlSlot = timeSlots.filter(sl => sl.slotType == 2);
        if(unavlSlot != undefined && unavlSlot.length > 0)
        {
            var uslot = unavlSlot[0].bookedSlots.filter(el => moment(el.scheduledstart).format('h:mm a') === slotItem && 
            moment(el.scheduledstart).format('MM/DD/YYYY') == moment(date).format('MM/DD/YYYY'));
            if(uslot!=undefined && uslot.length>0){
                var slotData = uslot[0];
                var endDate = new Date(slotData.scheduledend);
                var startDate = new Date(slotData.scheduledstart); 
                var hours = moment.duration(moment(endDate).diff(moment(startDate))).asHours();
                if(hours == 1){                    
                    var dTitle = moment(new Date(slotData.scheduledstart)).format('h:mm a');                      
                    var nextSlot = moment(new Date(slotData.scheduledstart)).add(1, 'hours').format('h:mm a');
                    tempDate+= '<li class= "timecard-1"><div class="single-schedule"><input type="radio" name="calendarview" data-bs-toggle="modal" data-bs-target="#edit-unavailability" id="'+slotData.activityid+'" title="'+dTitle+'">';
                    tempDate+= '<label for="calendarview" class="schedule-card unavailable-card">';  
                    tempDate+= '<div class="schedule-time">'+dTitle+' - '+nextSlot+'</div>'; 
                    tempDate+= '<div class="schedule-services '+slotData.subject+'">Unavailable - '+slotData.subject+'</div>';
                    tempDate+= '<div class="schedule-representative">Me</div>';
                    tempDate+= '</label></div></li>'; 
                    return;
                } else{                                  
                    unavlDisplayObj = checkAndCreateMultiSlotCalendarBlocks(slotData,unavlDisplayObj);
                    tempDate+=  unavlDisplayObj.tempStr; 
                    return;                     
                }          
            }           
        }
        if(slotItem.indexOf(unavlDisplayObj.blockedLiSlot)!== -1){
            tempDate+= '<li></li>';
        }        
    });
    
    tempDate+= '</ul>';  
    $('#date-'+dateId).append(tempDate);   
    
}


    function checkSlots(day) {
        $("input[name='time']").prop("disabled", false);
        const today = new Date();
        var datepart = ("0" + today.getDate()).slice(-2);
        var slots = [8, 9, 10, 11, 12, 13, 14, 15, 16];
        var dateSlotCard = $("#slot_" + day);
        $("div.time span").text("Available");

        var bSlots = unAvailableSlots.find(item => item.day === day);
        if (bSlots != undefined) {
            var tSlots = bSlots.bookedSlots;
            $(tSlots).each(function(index, elem) {
                var slotTime = moment(new Date(elem.appointmentStartDate + " UTC")).format("H:mm");
                var slotEndTime = moment(new Date(elem.appointmentEndDate + " UTC")).format("H:mm");
                var slotTimetxt = parseInt(slotTime.substring(0, slotTime.indexOf(":")));
                var slotEndTimetxt = parseInt(slotEndTime.substring(0, slotEndTime.indexOf(":")));
                for (i = slotTimetxt; i < slotEndTimetxt; i++) {
                    var idx = i > 12 ? i - 12 : i;
                    var timeSlotObj = $("#tslot_" + idx);
                    timeSlotObj.prop("disabled", true);
                    var slotText = $(timeSlotObj).next().find("span");
                    slotText.text("Unavailable");
                }
            });
        }
        if (day == datepart) {
            var availableSlots = currentDayAvailableSlots;
            var currMinutes = today.getMinutes();
            var currHr = currMinutes > 0 ? today.getHours() + 1 : today.getHours();
            $(slots).each(function(index, elem) {
                if (elem < currHr) {
                    var currSlotHr = elem > 12 ? elem - 12 : elem;
                    availableSlots = parseInt(availableSlots) - 1;
                    var currTimeSlotObj = $("#tslot_" + currSlotHr);
                    currTimeSlotObj.prop("disabled", true);
                    var slotText = $(currTimeSlotObj).next().find("span");
                    slotText.text("Unavailable");
                }
            });
            dateSlotCard.text(availableSlots + " slots available");
        }
    }


function checkAndCreateMultiSlotCalendarBlocks(slotData,unavlDisplayObj){
    var endDate = new Date(slotData.scheduledend);
    var startDate = new Date(slotData.scheduledstart); 
    var hours = moment.duration(moment(endDate).diff(moment(startDate))).asHours();
    var totalslots = Math.floor(hours/8);     
    var currSlotDate = moment(new Date(slotData.scheduledstart)).format('MM/DD/YYYY');
    var endSlotTime = "4:00 pm";
    var currDaySlotEndDateTime = currSlotDate + " " + endSlotTime; 
    var startSlotDateTime = moment(new Date(slotData.scheduledstart)).format('MM/DD/YYYY h:mm a'); 
    var endSlotDateTime = moment(new Date(slotData.scheduledend)).format('MM/DD/YYYY h:mm a');
    var currDayDur = moment.duration(moment(currDaySlotEndDateTime).diff(moment(startSlotDateTime))).asHours();    
    var liStr = "";
    unavlDisplayObj.tempStr = "";
    unavlDisplayObj.blockedLiSlot="";
    if(totalslots<=1 && hours<=currDayDur){ 
        var className = "timecard-" + hours;
        var dTitle = moment(new Date(slotData.scheduledstart)).format('h:mm a');                      
        var nextSlot = moment(new Date(slotData.scheduledstart)).add(hours, 'hours').format('h:mm a');
        liStr+= '<li class="'+ className + '"><div class="single-schedule"><input type="radio" name="calendarview" data-bs-toggle="modal" data-bs-target="#edit-unavailability" id="'+slotData.activityid+'" title="'+dTitle+'">';
        liStr+= '<label for="calendarview" class="schedule-card unavailable-card">';  
        liStr+= '<div class="schedule-time">'+dTitle+' - '+nextSlot+'</div>'; 
        liStr+= '<div class="schedule-services '+slotData.subject+'">Unavailable - '+slotData.subject+'</div>';
        liStr+= '<div class="schedule-representative">Me</div>';
        liStr+= '</label></div></li>'; 
        unavlDisplayObj.tempStr = liStr;
        var slotStr = dTitle;
        var stDt = moment(new Date(slotData.scheduledstart));
        for(i=0;i<hours; i++){
            slotStr += "," + stDt.add(1, 'hours').format('h:mm a');
        }
        unavlDisplayObj.blockedLiSlot = slotStr;
        return unavlDisplayObj;
    }
    else{
    } 
    return unavlDisplayObj ; 
         
}

