{% block main %}
<div class="everyday-banking-content">
  <ul>
    <li class="eb-icon eb-one"><strong id="meeting-date"></strong></li>
    <li class="eb-icon eb-two"><strong id="subcategory"></strong></li>
    <li class="eb-icon eb-thr"><strong id="contact-name"></strong>
      <span id="contact-email"></span>
      <span id="contact-phone"></span>
    </li>
    <li class="eb-fou"><strong id="descriptionText"></strong>
      <span id="description"></span>
    </li>
  </ul>
  <ul>
    <li class="eb-fiv"><a id="cancel-meeting-url">Cancel Meeting</a>
      <a id="reschedule-url">Reschedule</a>
    </li>
    <li class="eb-icon eb-six"><strong id="location-option"></strong>
      <span id="bank-name-location"></span>
      <span id="bank-name-address"></span>
    </li>
  </ul>
</div>
<script>
  var appointmentId = getUrlVars()["id"];
  var appointmentData;
  $(document).ready(function() {
    getAppointment();
  });
  function addDetailsToModal() {
    $('body').addClass('header-remove');
    $('.navbar.navbar-inverse.navbar-static-top').remove();
    $("#edit-calendar-modal", parent.document).css("height", "310px");
    var editAppointment = appointmentData;
    var meetTime = {
      todayDate: "",
      meetstart: "",
      meetend: ""
    }
    var appointmentguid = editAppointment.activityid;
    var cancelURL = window.parent.location.origin + '/admincancel/?id=' + appointmentguid;
    var rescheduleURL = window.parent.location.origin + '/adminreschedule/?id=' + appointmentguid;
    meetTime.todayDate = moment(new Date(editAppointment.scheduledstart)).format('MMM DD, YYYY');
    meetTime.meetstart = moment(new Date(editAppointment.scheduledstart)).format('h:mm a');
    meetTime.meetend = moment(new Date(editAppointment.scheduledend)).format('h:mm a');
    $('#meeting-date').html(meetTime.todayDate + ' ' + meetTime.meetstart + ' - ' + meetTime.meetend);
    $('#subcategory').html(editAppointment.subservicecategory);
    $('#bank-name-location').html(editAppointment.bankname);
    $('#bank-name-address').html(editAppointment.bankaddress);
    $('#contact-name').html(editAppointment.msdynce_firstname + ' ' + editAppointment.msdynce_lastname);
    $('#contact-email').html(editAppointment.msdynce_emailaddress);
    $('#contact-phone').html(editAppointment.phone);
    $("#descriptionText").html("<strong>Is there anything else you would like us to know?</strong>");
    $('#description').html(decodeURIComponent(editAppointment.description));
    $('#location-option').html(editAppointment.locationoption);
    $('#cancel-meeting-url').attr("href", cancelURL);
    $('#reschedule-url').click(function() {
      $("#edit-calendar", parent.document).removeClass("in"); //C1 edit
      $(".modal-backdrop", parent.document).remove();
      window.top.location.replace(rescheduleURL);
    });
  }
  function getUrlVars() {
    var vars = [],
      hash;
    var hashes = window.location.href.slice(window.location.href.indexOf('?') + 1).split('&');
    for (var i = 0; i < hashes.length; i++) {
      hash = hashes[i].split('=');
      vars.push(hash[0]);
      vars[hash[0]] = hash[1];
    }
    return vars;
  }
  function getAppointment() {
    $.ajax({
      url: '/_api/appointments(' + appointmentId + ')',
      type: "GET",
      contentType: 'application/json',
      success: function(response) {
        appointmentData = response;
        if (appointmentData.msdyn_bankappointmenttype == 192350001) {
          $("#location-option").value("Virtual Meeting");
          $("#bank-name-location").text("Conference Software");
          $("bank-name-address").append('<button type="button" class="virtual-meeting-url" aria-label="Virtual Meeting Url" href=${appointmentData.onlinemeetingjoinurl}>Join</button>')
        } else {
          $("#location-option").text("At the bank branch");
          $("#bank-name-location").text(appointmentData["_msdyn_location_value@OData.Community.Display.V1.FormattedValue"]);
          var branchId = appointmentData["_msdyn_location_value"];
          fetchBranch(branchId);
        }
        addDetailsToModal();
        return response;
      },
      error: function(e) {
        alert(e.statusText);
      }
    });
  }
  function fetchBranch(branchId) {
    $.ajax('Loading...', {
      type: "GET",
      url: `/_api/msdyn_locations(${branchId})?$select=msdyn_name,msdyn_branchaddress`,
      contentType: "application/json",
      success: function(data) {
        $("#bank-name-address").text(data.msdyn_branchaddress);
      }
    });
  }
</script>
{% endblock %}

